crf:query.crf:statement:
SET NOCOUNT ON; DECLARE @StudySummary NVARCHAR(500); DECLARE @Status NVARCHAR(MAX); DECLARE @Site NVARCHAR(500);
DECLARE @Statuses TABLE ( Status INTEGER PRIMARY KEY )
IF (@ItemID IS NOT NULL) BEGIN DECLARE @ViewParams TABLE ( FieldName VARCHAR(40), FieldValue NVARCHAR(1000) )
INSERT INTO @ViewParams SELECT WSFName, WSFValue FROM WTCRFStateFields WHERE WSFStateID=@ItemID;
SELECT @StudySummary=FieldValue FROM @ViewParams WHERE FieldName='StudySummary'; SELECT @Status=FieldValue FROM @ViewParams WHERE FieldName='Status'; SELECT @Site=FieldValue FROM @ViewParams WHERE FieldName='Site';
END;
INSERT INTO @Statuses(Status) SELECT ItemID FROM dbo.get_IntegerTable(@Status);
SELECT DISTINCT VSA.SIdentifier, VSA.SREventID, CONVERT(NVARCHAR(12),FIRSTVISIT.TStartDate,106) AS FPVStartDate, CONVERT(NVARCHAR(12),FIRSTVISIT.TStartDate,108) AS FPVStartTime, (SELECT WUIScript FROM WTCRFUserInterface WHERE WUIAreaID=1007 AND WUIName='Status ' + FIRSTVISIT.RESTName) AS FPVStatusStyle, FIRSTVISIT.RESTName AS FPVRESTName, VSA.StudySummary AS FPVStudySummary, FIRSTVISIT.PSummary AS FPVPSummary, FIRSTVISIT.VisitTypeSummary AS FPVVistTypeSummary, CONVERT(NVARCHAR(12),LASTVISIT.TStartDate,106) AS LVStartDate, CONVERT(NVARCHAR(12),LASTVISIT.TStartDate,108) AS LVStartTime, (SELECT WUIScript FROM WTCRFUserInterface WHERE WUIAreaID=1007 AND WUIName='Status ' + LASTVISIT.RESTName) AS LVStatusStyle, LASTVISIT.RESTName AS LVRESTNAME, VSA.StudySummary AS LVStudySummary, LASTVISIT.PSummary AS LVPSummary, LASTVISIT.VisitTypeSummary AS LVVisitTypeSummary, FIRSTVISIT.LocationName AS FLocation, LASTVISIT.LocationName AS LLocation, CONVERT(NVARCHAR(12),FIRSTRECRUITED.PSRecruitmentDate,106) AS FirstRecruited FROM view_StudyAmendments VSA INNER JOIN (SELECT DISTINCT WAreaID FROM view_UserRights WHERE (WRView=1) AND (WUserID=@UserID)) STUDY ON VSA.REAreaID=STUDY.WAreaID
OUTER APPLY (SELECT TOP 1 TStartDate, RESTName, REStatusID, PSummary, VisitTypeName, VisitTypeSummary, LocationName, RAreaID FROM view_PatientEvents VPE LEFT OUTER JOIN WTCRFPatients WP ON WP.PatientID=VPE.PatientID WHERE VPE.SREventID=VSA.SREventID AND ((@Status IS NULL) OR (REStatusID IN (SELECT Status FROM @Statuses))) ORDER BY VPE.TStartDate ASC) FIRSTVISIT
OUTER APPLY (SELECT TOP 1 TStartDate, RESTName, REStatusID, PSummary, VisitTypeName, VisitTypeSummary, LocationName, RAreaID FROM view_PatientEvents VPE LEFT OUTER JOIN WTCRFPatients WP ON WP.PatientID=VPE.PatientID WHERE VPE.SREventID=VSA.SREventID AND ((@Status IS NULL) OR (REStatusID IN (SELECT Status FROM @Statuses))) ORDER BY VPE.TStartDate DESC) LASTVISIT
OUTER APPLY (SELECT TOP 1 PSRecruitmentDate, VPS.RAreaID FROM view_PatientStudies VPS INNER JOIN view_PatientEvents VPE ON VPE.PatientID=VPS.PatientID AND VPE.SREventID=VPS.SREventID WHERE VPS.SREventID=VSA.SREventID AND PSSTSubTypeID=1 ORDER BY PSRecruitmentDate ASC) FIRSTRECRUITED
INNER JOIN (SELECT DISTINCT WAreaID FROM view_UserRights WHERE (WRView=1) AND (WUserID=@UserID)) PATIENTSLAST ON FIRSTVISIT.RAreaID=PATIENTSLAST.WAreaID INNER JOIN (SELECT DISTINCT WAreaID FROM view_UserRights WHERE (WRView=1) AND (WUserID=@UserID)) PATIENTSFIRST ON LASTVISIT.RAreaID=PATIENTSFIRST.WAreaID INNER JOIN (SELECT DISTINCT WAreaID FROM view_UserRights WHERE (WRView=1) AND (WUserID=@UserID)) PATIENTSRECRUIT ON FIRSTRECRUITED.RAreaID=PATIENTSRECRUIT.WAreaID WHERE SAStatusSubID IS NOT NULL AND (LASTVISIT.VisitTypeName IS NOT NULL OR FIRSTVISIT.VisitTypeName IS NOT NULL) AND ((@Status IS NULL) OR (FIRSTVISIT.REStatusID IN (SELECT Status FROM @Statuses)) OR (LASTVISIT.REStatusID IN (SELECT Status FROM @Statuses))) AND ((@StudySummary IS NULL) OR (VSA.StudySummary LIKE @StudySummary)) AND ((@Site IS NULL) OR ((FIRSTVISIT.LocationName IN (SELECT ResourceName FROM view_Resources WHERE ROwnerID=@Site)) OR (LASTVISIT.LocationName IN (SELECT ResourceName FROM view_Resources WHERE ROwnerID=@Site)))) ORDER BY VSA.SIdentifier, FPVStartDate, FPVStartTime
SET @TotalRecords=@@ROWCOUNT;
